const getCountryCounts = hits => {
  let dataProvider = {};
  let dpArray = []
  hits.forEach((article) => {
    console.log(article.country);
    if (dataProvider[article.country]) {
      dataProvider[article.country]++;
    } else {
      dataProvider[article.country] = 1;
    }
  });
  for (let country in dataProvider) {
    dpArray.push({
      country: country,
      hits: dataProvider[country]
    })
  }
  return dpArray.sort((a, b) => {
    return b.hits - a.hits;
  });
}

